import React, { useState, useEffect} from 'react';
import axios from 'axios';
import {Button, Spinner} from 'react-bootstrap';
import { NavLink } from "react-router-dom";
import './styles.css';

function App(props){
  const [data, setData] = useState({});
  const [showLoading, setShowLoading] = useState(true);
  const apiUrl = "http://localhost:3000/run";
  const dataFrom = {
    learningRate: parseFloat(props.location.state.learningRate),
    epochNum: parseInt(props.location.state.epochNum),
    sepal_length: parseFloat(props.location.state.sepal_length),
    sepal_width: parseFloat(props.location.state.sepal_width),
    petal_length: parseFloat(props.location.state.petal_length),
    petal_width: parseFloat(props.location.state.petal_width),
  };
  //runs once after the first rendering of page
  useEffect(() => {
    const fetchData = async () => {
    axios
    .post(apiUrl, dataFrom)
    .then(result => {
      console.log('result.data:' , result.data)
      setData(result.data)
      setShowLoading(false)
    }).catch((error) => {
      console.log('error in fetchData:', error)
    });
  };
  fetchData();
}, []);

return(
  <div>
    { showLoading === false
    ? <div>
        {showLoading && <Spinner animation="border" role="status">
          <span classname="sr-only">Loading...</span>
          </Spinner>}
          
          <h1>Prediction Results</h1>
          <h2> the values for species will be:</h2>
          <li>setosa: 1,0,0</li>
          <li>virginica: 0,1,0</li>
          <li>versicolor: 0,0,1 </li>

          <table className="Results-table">
          <thead>
            <tr>
              <th>Learning Rate</th>
              <th>Epoch Number</th>
              <th>Setosa</th>
              <th>Virginica</th>
              <th>Versicolor</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="App-td">
                {dataFrom.learningRate}
              </td>
              <td className="App-td">
              {dataFrom.epochNum}
              </td>
              <td className="App-td">
                {data.row1.map((value, index) =>(
                    <p key={index}>{value}</p>
                ))}
              </td>
              <td className="App-td">
                {data.row2.map((value, index) =>(
                    <p key={index}>{value}</p>
                ))}
              </td>
              <td className="App-td">
                {data.row3.map((value, index) =>(
                    <p key={index}>{value}</p>
                ))}
              </td>
            </tr>
          </tbody>
          </table>
          <div className="text-center">
            <Button
            style={{
              background: "white",
              color: "black",
              padding: "15px 32px",
              textAlign: "center",
              textDecoration: "none",
              display: "inline-block",
              fontSize: "15px",
              fontWeight: "bold"
            }}
            type="reset"
            to="/"
            activeClassName="active"
            >GO BACK
            </Button>
            </div>
  </div>
  :
  <div>
    {showLoading && <Spinner animation="border" role="status">
      <span className="sr-only">Waiting for results...</span>
      </Spinner>}
  </div>

}
</div>
);
}

export default App;
